import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ApiService } from '../../services/api.service';

@Component({
  selector: 'app-food-items',
  templateUrl: './order-list.component.html',
  styleUrls: ['./order-list.component.css']
})
export class FoodItemsComponent implements OnInit {
  foods: any[] | null = null;
  selectedFood: any = null;

  constructor(
    private apiService: ApiService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadFoods();
  }

  loadFoods(): void {
    this.foods = null; // Show loading state
    this.apiService.getFoods().subscribe({
      next: (foods: any[]) => {
        this.foods = foods;
      },
      error: (error) => {
        console.error('Error loading foods:', error);
        this.foods = []; // Show no foods message on error
      }
    });
  }

  onOrder(food: any): void {
    this.selectedFood = food;
    this.router.navigate(['/address'], {
      queryParams: { foodId: food._id }
    });
  }
}
<div class="food-container">
  <h2 class="section-title">Available Food Items</h2>

  <div class="back-btn-container">
    <button class="btn btn-back" routerLink="/admin">← Back to Admin</button>
  </div>

  <div class="loading" *ngIf="!foods">Loading food items...</div>

  <div class="food-grid" *ngIf="foods && foods.length > 0; else noFoods">
    <div class="food-card" *ngFor="let food of foods">
      <div class="food-image">
        <img [src]="food.imageUrl" [alt]="food.name" />
      </div>

      <div class="food-info">
        <h3>{{ food.name }}</h3>
        <p class="price">₹{{ food.price }}</p>
        <p class="details">Qty: {{ food.quantity }} | {{ food.distance }} km away</p>

        <button
          class="btn btn-order"
          [disabled]="food.quantity <= 0"
          (click)="onOrder(food)">
          {{ food.quantity > 0 ? 'Order Now' : 'Out of Stock' }}
        </button>
      </div>
    </div>
  </div>

  <ng-template #noFoods>
    <div class="no-foods">
      <p>No food items available at the moment.</p>
      <p>Check back later!</p>
    </div>
  </ng-template>
</div>
