import { Component, OnInit } from '@angular/core';
import { AdminService } from '../../../services/admin.service';
import { AuthService } from '../../../services/auth.service';
import { Router } from '@angular/router';

// Define interface for admin order component separately
interface AdminOrder {
  _id: string;
  foodId: {
    _id?: string; // Making optional since backend populates with full object
    name: string;
    price: number;
  };
  buyerName: string;
  address: string;
  deliveryOption: string;
  paymentStatus: string;
  createdAt: string;
}

@Component({
  selector: 'app-admin-orders',
  templateUrl: './admin-orders.component.html',
  styleUrls: ['./admin-orders.component.css']
})
export class AdminOrdersComponent implements OnInit {
  orders: AdminOrder[] = [];
  filteredOrders: AdminOrder[] = [];
  loading = true;
  currentAdmin: any = null;
  statusFilter = 'all';
  searchTerm = '';

  constructor(
    private adminService: AdminService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit() {
    this.currentAdmin = this.authService.getCurrentAdmin();
    this.loadOrders();
  }

  loadOrders() {
    this.loading = true;
    this.adminService.getAllOrders().subscribe({
      next: (data) => {
        this.orders = data;
        this.applyFilters();
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading orders:', error);
        this.loading = false;
      }
    });
  }

  applyFilters() {
    this.filteredOrders = this.orders.filter(order => {
      const matchesStatus = this.statusFilter === 'all' || order.paymentStatus === this.statusFilter;
      const matchesSearch = !this.searchTerm ||
        order.buyerName.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        order.foodId.name.toLowerCase().includes(this.searchTerm.toLowerCase());
      return matchesStatus && matchesSearch;
    });
  }

  onStatusFilterChange(status: string) {
    this.statusFilter = status;
    this.applyFilters();
  }

  onSearchChange(searchTerm: string) {
    this.searchTerm = searchTerm;
    this.applyFilters();
  }

  updateOrderStatus(orderId: string, newStatus: string) {
    this.adminService.updateOrderStatus(orderId, newStatus).subscribe({
      next: (updatedOrder) => {
        // Update the order in the local array
        const index = this.orders.findIndex(order => order._id === orderId);
        if (index !== -1) {
          this.orders[index] = updatedOrder;
          this.applyFilters();
        }
      },
      error: (error) => {
        console.error('Error updating order status:', error);
        alert('Failed to update order status. Please try again.');
      }
    });
  }

  getStatusClass(status: string): string {
    switch (status) {
      case 'completed': return 'status-completed';
      case 'pending': return 'status-pending';
      case 'cancelled': return 'status-cancelled';
      default: return '';
    }
  }

  formatDate(dateString: string): string {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/admin/login']);
  }

  navigateTo(section: string) {
    this.router.navigate(['/admin', section]);
  }
}
<div class="orders-container">
  <!-- Header -->
  <header class="page-header">
    <div class="header-left">
      <h1>Order Management</h1>
      <p>Welcome back, {{ currentAdmin?.username }}</p>
    </div>
    <button class="logout-btn" (click)="logout()">Logout</button>
  </header>

  <!-- Navigation -->
  <nav class="page-nav">
    <button class="nav-btn" (click)="navigateTo('dashboard')">Dashboard</button>
    <button class="nav-btn active">Orders</button>
    <button class="nav-btn" (click)="navigateTo('products')">Products</button>
    <button class="nav-btn" (click)="navigateTo('customers')">Customers</button>
  </nav>

  <!-- Filters and Search -->
  <div class="filters-section">
    <div class="filter-controls">
      <div class="search-bar">
        <input
          type="text"
          placeholder="Search by customer or food..."
          [(ngModel)]="searchTerm"
          (input)="onSearchChange(searchTerm)"
          class="search-input">
        <i class="fas fa-search search-icon"></i>
      </div>

      <div class="status-filters">
        <button
          class="filter-btn"
          [class.active]="statusFilter === 'all'"
          (click)="onStatusFilterChange('all')">All Orders</button>
        <button
          class="filter-btn"
          [class.active]="statusFilter === 'pending'"
          (click)="onStatusFilterChange('pending')">Pending</button>
        <button
          class="filter-btn"
          [class.active]="statusFilter === 'completed'"
          (click)="onStatusFilterChange('completed')">Completed</button>
      </div>
    </div>

    <div class="results-summary">
      Showing {{ filteredOrders.length }} of {{ orders.length }} orders
    </div>
  </div>

  <!-- Orders Table -->
  <div class="orders-table-container" *ngIf="!loading">
    <table class="orders-table" *ngIf="filteredOrders.length > 0; else noOrders">
      <thead>
        <tr>
          <th>Order ID</th>
          <th>Customer</th>
          <th>Food Item</th>
          <th>Price</th>
          <th>Delivery</th>
          <th>Status</th>
          <th>Date & Time</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let order of filteredOrders">
          <td>#{{ order._id.slice(-6) }}</td>
          <td>
            <div class="customer-info">
              <strong>{{ order.buyerName }}</strong>
              <br>
              <small>{{ order.address }}</small>
            </div>
          </td>
          <td>{{ order.foodId.name }}</td>
          <td class="price-cell">₹{{ order.foodId.price }}</td>
          <td>
            <span class="delivery-badge" [class]="order.deliveryOption">
              {{ order.deliveryOption === 'walk' ? 'Walk-in' : 'Home Delivery' }}
            </span>
          </td>
          <td>
            <span class="status-badge" [class]="getStatusClass(order.paymentStatus)">
              {{ order.paymentStatus | titlecase }}
            </span>
          </td>
          <td>{{ formatDate(order.createdAt) }}</td>
          <td>
            <div class="actions-cell" *ngIf="order.paymentStatus === 'pending'">
              <button
                class="action-btn complete-btn"
                (click)="updateOrderStatus(order._id, 'completed')">
                Mark Complete
              </button>
            </div>
            <span class="no-action" *ngIf="order.paymentStatus !== 'pending'">
              No action needed
            </span>
          </td>
        </tr>
      </tbody>
    </table>

    <ng-template #noOrders>
      <div class="no-orders">
        <i class="fas fa-shopping-cart empty-icon"></i>
        <h3>No orders found</h3>
        <p>Try adjusting your filters or check back later.</p>
      </div>
    </ng-template>
  </div>

  <!-- Loading State -->
  <div class="loading" *ngIf="loading">
    <div class="spinner"></div>
    <p>Loading orders...</p>
  </div>
</div>
