import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from '../../services/api.service';

@Component({
  selector: 'app-address',
  templateUrl: './address.component.html',
  styleUrls: ['./address.component.css']
})
export class AddressComponent implements OnInit {
  foodId: string = '';
  buyerName: string = '';
  address: string = '';
  deliveryOption: string = 'walk';

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private apiService: ApiService
  ) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      this.foodId = params['foodId'];
    });
  }

  onSubmit(): void {
    if (this.buyerName && this.address && this.foodId) {
      const orderData = {
        foodId: this.foodId,
        buyerName: this.buyerName,
        address: this.address,
        deliveryOption: this.deliveryOption
      };

      this.apiService.placeOrder(orderData).subscribe({
        next: (order: any) => {
          alert('Order placed successfully!');
          this.router.navigate(['/payment'], {
            queryParams: { orderId: order._id }
          });
        },
        error: (error) => {
          console.error('Error placing order:', error);
          alert('Failed to place order. Please try again.');
        }
      });
    } else {
      alert('Please fill in all required fields.');
    }
  }

  goBack(): void {
    this.router.navigate(['/order']);
  }
}
<div class="address-container">
  <h2 class="section-title">Enter Delivery Details</h2>

  <div class="form-container">
    <p style="text-align: center; color: #666; margin-bottom: 20px;">Food ID: {{ foodId }}</p>

    <div class="form-group">
      <label for="buyerName">Your Name:</label>
      <input
        type="text"
        id="buyerName"
        [value]="buyerName"
        (input)="buyerName = $any($event.target).value"
        name="buyerName"
        placeholder="Enter your full name"
        required
        class="form-control">
    </div>

    <div class="form-group">
      <label for="address">Delivery Address:</label>
      <textarea
        id="address"
        [value]="address"
        (input)="address = $any($event.target).value"
        name="address"
        placeholder="Enter your complete address"
        rows="3"
        required
        class="form-control"></textarea>
    </div>

    <div class="form-group">
      <label>Delivery Option:</label>
      <div class="radio-group">
        <label class="radio-label">
          <input
            type="radio"
            name="deliveryOption"
            value="walk"
            [checked]="deliveryOption === 'walk'"
            (change)="deliveryOption = 'walk'"
            class="form-check-input">
          Pick up myself (Walk)
        </label>
        <label class="radio-label">
          <input
            type="radio"
            name="deliveryOption"
            value="home"
            [checked]="deliveryOption === 'home'"
            (change)="deliveryOption = 'home'"
            class="form-check-input">
          Home delivery
        </label>
      </div>
    </div>

    <div class="button-group">
      <button type="button" class="btn btn-back" (click)="goBack()">← Back</button>
      <button type="button" class="btn btn-primary" (click)="onSubmit()">Continue to Payment</button>
    </div>
  </div>
</div>
