import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Cook Next Door';
}
<div class="home-container">
  <h1 class="app-title">Cook Next Door</h1>
  <div class="buttons-container">
    <button class="btn btn-primary" routerLink="/order">Order Food</button>
    <button class="btn btn-secondary" routerLink="/sell">Sell Food</button>
  </div>
</div>
