import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Cook Next Door';

  constructor(private router: Router) {}

  navigateToOrder() {
    this.router.navigate(['/order']);
  }
}
<div class="home-container">
  <h1 class="app-title">Cook Next Door</h1>
  <p style="color: #666; margin-bottom: 20px;">Connect people in your neighborhood to buy and sell homemade food</p>
  <div class="buttons-container">
    <button (click)="navigateToOrder()" class="btn btn-primary">Order Food</button>
    <button routerLink="/order" class="btn btn-secondary">Add/Sell Food</button>
  </div>

  <div class="features">
    <div class="feature">
      <h3>üè† Local Delivery</h3>
      <p>Support local businesses and enjoy fresh homemade food</p>
    </div>
    <div class="feature">
      <h3>üöö Fast Pickup</h3>
      <p>Walk to your neighbor and pickup your favorite dishes</p>
    </div>
    <div class="feature">
      <h3>üí∞ Easy Payments</h3>
      <p>Multiple payment options including cash on delivery</p>
    </div>
  </div>
</div>

<router-outlet></router-outlet>
