import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from '../../services/api.service';

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.css']
})
export class PaymentComponent implements OnInit {
  orderId: string = '';
  paymentAmount: number = 0;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private apiService: ApiService
  ) { }

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      this.orderId = params['orderId'];
    });
  }

  processPayment(): void {
    if (this.orderId) {
      this.apiService.processPayment(this.orderId).subscribe({
        next: (result) => {
          alert('Payment successful! Your order has been confirmed.');
          this.router.navigate(['/']);
        },
        error: (error) => {
          console.error('Payment error:', error);
          alert('Payment failed. Please try again.');
        }
      });
    }
  }

  goBack(): void {
    this.router.navigate(['/address']);
  }
}
<div class="payment-container">
  <h2 class="section-title">Payment</h2>

  <div class="payment-form">
    <div class="payment-card">
      <div class="card-header">
        <h3>Complete Your Payment</h3>
      </div>

      <div class="card-body">
        <div class="amount-display">
          <span class="amount-label">Total Amount:</span>
          <span class="amount-value">₹{{ paymentAmount || 0 }}</span>
        </div>

        <div class="payment-details">
          <p>This is a mock payment system.</p>
          <p>Click "Pay Now" to complete your order.</p>
        </div>

        <div class="payment-methods">
          <div class="payment-option">
            <input type="radio" id="card" name="payment" checked>
            <label for="card">Credit/Debit Card</label>
          </div>

          <div class="payment-option">
            <input type="radio" id="upi" name="payment">
            <label for="upi">UPI / Digital Wallet</label>
          </div>

          <div class="payment-option">
            <input type="radio" id="cod" name="payment">
            <label for="cod">Cash on Delivery</label>
          </div>
        </div>

        <div class="button-group">
          <button type="button" class="btn btn-back" (click)="goBack()">← Back</button>
          <button type="button" class="btn btn-primary" (click)="processPayment()">Pay Now</button>
        </div>
      </div>
    </div>
  </div>
</div>
