import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ApiService } from '../../services/api.service';

interface Food {
  _id: string;
  name: string;
  price: number;
  quantity: number;
  imageUrl: string;
  distance: number;
  preorder: boolean;
}

@Component({
  selector: 'app-order-list',
  templateUrl: './order-list.component.html',
  styleUrls: ['./order-list.component.css']
})
export class OrderListComponent implements OnInit {
  foods: Food[] = [];
  selectedFood: Food | null = null;

  constructor(
    private apiService: ApiService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadFoods();
  }

  loadFoods(): void {
    this.apiService.getFoods().subscribe({
      next: (foods) => {
        this.foods = foods;
      },
      error: (error) => {
        console.error('Error loading foods:', error);
      }
    });
  }

  onOrder(food: Food): void {
    this.selectedFood = food;
    this.router.navigate(['/address'], {
      queryParams: { foodId: food._id }
    });
  }
}
<div class="order-container">
  <h2 class="section-title">Available Food Items</h2>

  <div class="back-btn-container">
    <button class="btn btn-back" routerLink="/">← Back to Home</button>
  </div>

  <div class="food-grid" *ngIf="foods.length > 0; else noFoods">
    <div class="food-card" *ngFor="let food of foods">
      <div class="food-image">
        <img [src]="food.imageUrl" [alt]="food.name" />
      </div>

      <div class="food-info">
        <h3>{{ food.name }}</h3>
        <p class="price">₹{{ food.price }}</p>
        <p class="details">Qty: {{ food.quantity }} | {{ food.distance }} km away</p>

        <button
          class="btn btn-order"
          [disabled]="food.quantity <= 0"
          (click)="onOrder(food)">
          {{ food.quantity > 0 ? 'Order Now' : 'Out of Stock' }}
        </button>
      </div>
    </div>
  </div>

  <ng-template #noFoods>
    <div class="no-foods">
      <p>No food items available at the moment.</p>
      <p>Check back later!</p>
    </div>
  </ng-template>
</div>
